mapper.select:
select a.id, a.doc_id, a.student_id, a.student_name, a.gaokao_year, a.love_subject, a.channel, a.if_listener,
a.if_success, a.if_error, a.if_introduction, a.introduction_type, a.introduction_name, a.u_time,
a.c_time,a.student_phone,b.doc_name,d.org_name,a.backup
from hf_order a
inner join hrip_doc b on a.doc_id = b.doc_id
inner join hrip_org_doc c on a.doc_id = c.doc_id
inner join hrip_org d on c.org_id = d.org_id


     and a.student_id = #{studentId,jdbcType=VARCHAR}


      and  a.student_name = #{studentName,jdbcType=VARCHAR}



      and a.c_time = #{cTime,jdbcType=TIMESTAMP}



      and a.student_phone =  #{studentPhone,jdbcType=CHAR}


      and b.doc_name =  #{docName,jdbcType=VARCHAR}


      and d.org_name =  #{orgName,jdbcType=VARCHAR}


        and UNIX_TIMESTAMP(a.c_time) >=  UNIX_TIMESTAMP(#{ctime_from,jdbcType=VARCHAR})


        and UNIX_TIMESTAMP(a.c_time) <=  UNIX_TIMESTAMP(#{ctime_to,jdbcType=VARCHAR})  + 60*60*24-1


      and a.doc_id

        #{f_doc_id}


  and a.valid = 1

order by a.doc_id,a.c_time desc
